package com.aaludra.basicprogram;
/* declared meaningful variable name*/
public class DatatypesExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		String s= "Sting";
		float f =24.45f;
		double d= 34.445454d;
		char c= 'f';
		long l= 123456789l;
		System.out.println("The given integer is - " + i );
		System.out.println("The given float value is - " + f);
		System.out.println("The given double value is - " + d);
		System.out.println("The given char is - " + c);
		System.out.println("The given long  value is - "+ l);
		System.out.println("The given string value is - "+ s);
			}

	}


